const d=document.getElementById("theme-toggle"),n=document.documentElement;d.addEventListener("click",()=>{const s=n.dataset.theme==="dark"?"light":"dark";n.dataset.theme=s});const e=document.getElementById("header");window.addEventListener("scroll",()=>{const t=window.scrollY;t>=200?e.classList.add("headNoActive"):e.classList.remove("headNoActive"),t>=300?e.classList.add("headActive"):e.classList.remove("headActive")});const c=document.getElementById("year");c.textContent=new Date().getFullYear();
